import { useState } from "react";
import "./App.css";
import { v4 as uuidv4 } from "uuid";

function App() {
  // 최초값으로 객체로 이루어진 배열을 넣어야함
  const [todos, setTodos] = useState(initialState);
  return (
    <>
      <header style={{ textAlign: "center" }}>
        <h1>My Todo List</h1>
      </header>
      <main>
        <section
          style={{
            border: "1px solid black",
            padding: "30px",
            backgroundColor: "#eee",
            fontSize: "20px",
          }}
        >
          {" "}
          <span style={{}}>제목</span>
          <input
            style={{
              padding: "10px 15px",
              borderRadius: "7px",
              marginLeft: "15px",
            }}
            placeholder="제목을 입력해주세요"
          />
          <span style={{ marginLeft: "15px" }}>내용</span>
          <input
            style={{
              padding: "10px 15px",
              borderRadius: "7px",
              marginLeft: "15px",
            }}
            placeholder="내용을 입력해주세요"
          />
          <button
            style={{
              backgroundColor: "cornflowerblue",
              color: "white",
              padding: "5px 30px",
              borderRadius: "7px",
              marginLeft: "75px",
              fontSize: "15px",
            }}
          >
            추가하기
          </button>
        </section>

        <section style={{ display: "flex", margin: "20px", gap: "10px" }}>
          <span style={{ fontSize: "30px", marginTop: "70px" }}>Working</span>
          {todos
            .filter((item) => item.isDone === false)
            .map((item) => {
              return (
                <div
                  style={{
                    backgroundColor: "#eee",
                    width: "300px",
                    height: "200px",
                    borderRadius: "7px",
                    textAlign: "center",
                    border: "5px solid green",
                    marginLeft: "15px",
                  }}
                >
                  <h4
                    style={{
                      fontSize: "25px",
                      textAlign: "left",
                      padding: "5px",
                      marginLeft: "20px",
                    }}
                  >
                    {item.title}
                  </h4>
                  <p
                    style={{
                      fontSize: "18px",
                      textAlign: "left",
                      padding: "5px",
                      marginTop: "-20px",
                      marginLeft: "20px",
                    }}
                  >
                    {item.contetns}
                  </p>
                </div>
              );
            })}
        </section>
        <section style={{ display: "flex", margin: "20px", gap: "10px" }}>
          <span style={{ fontSize: "30px", marginTop: "70px" }}>Done</span>
          {todos
            .filter((item) => item.isDone === true)
            .map((item) => {
              return (
                <div
                  style={{
                    backgroundColor: "#eee",
                    width: "300px",
                    height: "200px",
                    borderRadius: "7px",
                    textAlign: "center",
                    border: "5px solid green",
                    marginLeft: "20px",
                  }}
                >
                  <h4
                    style={{
                      fontSize: "25px",
                      textAlign: "left",
                      padding: "5px",
                      marginLeft: "20px",
                    }}
                  >
                    {item.title}
                  </h4>
                  <p
                    style={{
                      fontSize: "18px",
                      textAlign: "left",
                      padding: "5px",
                      marginTop: "-20px",
                      marginLeft: "20px",
                    }}
                  >
                    {item.contetns}
                  </p>
                </div>
              );
            })}
        </section>
      </main>
    </>
  );
}
const initialState = [
  {
    title: "제목1",
    contetns: "내용1",
    isDone: false,
    id: uuidv4(),
  },
  {
    title: "제목2",
    contetns: "내용2",
    isDone: false,
    id: uuidv4(),
  },
  {
    title: "제목3",
    contetns: "내용3",
    isDone: false,
    id: uuidv4(),
  },
];
export default App;
