import { useState } from "react";
import "./App.css";

function App() {
  // 최초값으로 객체로 이루어진 배열을 넣어야함
  const [todos, setTodos] = useState(initialState);
  return (
    <>
      <header>헤더입니다.</header>
      <main>
        <section style={{ border: "1px solid black", padding: "20px" }}>
          {" "}
          제목 : <input placeholder="제목을 입력해주세요" />
          내용 : <input placeholder="내용을 입력해주세요" />
          <button>추가</button>
        </section>
        <section>
          {todos.map((item) => {
            return <h3>{item.contetns}</h3>;
          })}
        </section>
        <section>TodoList가 들어갈 곳(Done)</section>
      </main>
      <footer>footer 영역입니다.</footer>
    </>
  );
}
const initialState = [
  {
    title: "제목1",
    contetns: "내용1",
    isDone: false,
    id: 1,
  },
  {
    title: "제목2",
    contetns: "내용2",
    isDone: false,
    id: 2,
  },
  {
    title: "제목3",
    contetns: "내용3",
    isDone: false,
    id: 3,
  },
];
export default App;
